#!/bin/bash
# Quick fix for missing newapi.carambus.de nginx configuration

set -e

echo "=========================================="
echo "Fixing newapi.carambus.de nginx config"
echo "=========================================="
echo ""

# Create temporary nginx config for newapi.carambus.de
cat > /tmp/newapi.carambus.de.conf << 'EOF'
# NGINX Configuration for carambus_api
# Generated by Carambus Scenario System

upstream carambus_api_upstream {
    server unix:/var/www/carambus_api/shared/sockets/puma-production.sock;
}


# HTTP to HTTPS redirect
server {
    listen 80;
    server_name newapi.carambus.de;
    return 301 https://$server_name$request_uri;
}

# HTTPS server
server {
    listen 443 ssl http2;
    server_name newapi.carambus.de;

    root /var/www/carambus_api/current/public;

    ssl_certificate /etc/letsencrypt/live/newapi.carambus.de/fullchain.pem;
    ssl_certificate_key /etc/letsencrypt/live/newapi.carambus.de/privkey.pem;
    include /etc/letsencrypt/options-ssl-nginx.conf;
    ssl_dhparam /etc/letsencrypt/ssl-dhparams.pem;

    error_log /var/log/carambus_api/error.log debug;
    access_log /var/log/carambus_api/access.log combined;

    # Deny access to hidden files
    location ~ /\. {
        deny all;
    }

    # Let's Encrypt ACME challenge
    location ^~ /.well-known/acme-challenge/ {
        access_log off;
        log_not_found off;
        auth_basic off;
        root /var/www/carambus_api/current/public;
        autoindex off;
        index index.html;
        try_files $uri $uri/ =404;
    }

    # Favicon
    location = /favicon.ico {
        log_not_found off;
        access_log off;
        expires max;
        add_header Cache-Control "public, must-revalidate, proxy-revalidate";
    }

    # Robots.txt
    location = /robots.txt {
        add_header Content-Type text/plain;
        return 200 "User-agent: *\nDisallow: /\n";
    }

    # ActionCable WebSocket
    location /cable {
        proxy_pass http://carambus_api_upstream;
        proxy_http_version 1.1;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header Host $host;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "Upgrade";
        proxy_pass_request_headers on;
        proxy_buffering off;
        proxy_redirect off;
    }

    # Assets - serve directly if they exist
    location ~ ^/(assets|packs|images|javascripts|stylesheets|system)/ {
        expires max;
        add_header Cache-Control public;
        try_files $uri @carambus_api_upstream;
    }

    # Default location - proxy to Puma
    location / {
        try_files $uri @carambus_api_upstream;
    }

    location @carambus_api_upstream {
        proxy_pass http://carambus_api_upstream;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header Host $host;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_redirect off;
        
        # Timeouts
        proxy_connect_timeout 90;
        proxy_send_timeout 90;
        proxy_read_timeout 90;
        
        # Buffer settings
        proxy_buffer_size 4k;
        proxy_buffers 4 32k;
        proxy_busy_buffers_size 64k;
        proxy_temp_file_write_size 64k;
    }
}
EOF

echo "1. Installing newapi.carambus.de configuration..."
sudo cp /tmp/newapi.carambus.de.conf /etc/nginx/sites-available/newapi.carambus.de
sudo ln -sf /etc/nginx/sites-available/newapi.carambus.de /etc/nginx/sites-enabled/newapi.carambus.de
echo "✅ Configuration installed"
echo ""

echo "2. Creating necessary log directories..."
sudo mkdir -p /var/log/carambus_api
sudo chown -R www-data:www-data /var/log/carambus_api
echo "✅ Directories created"
echo ""

echo "3. Testing nginx configuration..."
if sudo nginx -t; then
    echo "✅ Configuration test passed"
else
    echo "❌ Configuration test failed!"
    echo "Rolling back..."
    sudo rm -f /etc/nginx/sites-enabled/newapi.carambus.de
    sudo rm -f /etc/nginx/sites-available/newapi.carambus.de
    exit 1
fi
echo ""

echo "4. Reloading nginx..."
if sudo systemctl reload nginx; then
    echo "✅ Nginx reloaded successfully"
else
    echo "⚠️  Reload failed, trying restart..."
    if sudo systemctl restart nginx; then
        echo "✅ Nginx restarted successfully"
    else
        echo "❌ Failed to restart nginx"
        exit 1
    fi
fi
echo ""

echo "5. Verifying configuration..."
echo "Enabled sites:"
ls -la /etc/nginx/sites-enabled/ | grep carambus
echo ""

echo "=========================================="
echo "✅ Fix complete!"
echo "=========================================="
echo ""
echo "Please test the sites:"
echo "  - https://newapi.carambus.de"
echo "  - https://new.carambus.de"
echo ""
echo "If there are still issues, check:"
echo "  sudo tail -f /var/log/carambus_api/error.log"
echo "  sudo tail -f /var/log/nginx/error.log"

